import React, { memo, useEffect, useContext, useState } from "react";
import { userContext } from "../pages/Home";

function Note() {
  const { setNoteUpdate } = useContext(userContext);
  const [data, setData] = useState([]);
  useEffect(() => {
    fetch("/api/getNotes/")
      .then((res) => res.json())
      .then((data) => {
        if (data.status) setData(data.message);
      });
  }, [setNoteUpdate]);

  const submit = (e, note_id) => {
    e.preventDefault();
    fetch("/api/deleteNote/", {
      method: "POST",
      body: JSON.stringify({ note_id }),
      headers: { "Content-Type": "application/json" },
    })
      .then((res) => res.json())
      .then((data) => {
        fetch("/api/getNotes/")
          .then((res) => res.json())
          .then((data) => {
            if (data.status) setData(data.message);
          });
      });
  };

  return (
    <>
      <button
        type="button"
        class="btn btn-primary"
        data-bs-toggle="modal"
        data-bs-target="#exampleModal"
      >
        Launch demo modal
      </button>

      <div
      style={{display: "block"}}
        class="modal fade"
        id="exampleModal"
        tabindex="1"
        aria-labelledby="exampleModalLabel"
        aria-hidden="true"
      >
        <div class="modal-dialog">
          <div class="modal-content">
            <div class="modal-header">
              <h1 class="modal-title fs-5" id="exampleModalLabel">
                Modal title
              </h1>
              <button
                type="button"
                class="btn-close"
                data-bs-dismiss="modal"
                aria-label="Close"
              ></button>
            </div>
            <div class="modal-body">...</div>
            <div class="modal-footer">
              <button
                type="button"
                class="btn btn-secondary"
                data-bs-dismiss="modal"
              >
                Close
              </button>
              <button type="button" class="btn btn-primary">
                Save changes
              </button>
            </div>
          </div>
        </div>
      </div>
      {data === [] ? (
        <p>Empty</p>
      ) : (
        data.map((el) => (
          <div className="card" key={el.note_id} style={{ width: "18rem" }}>
            <div className="card-body">
              <h5 className="card-title">{el.title}</h5>
              <h6
                className="card-subtitle mb-2 text-muted"
                style={{ fontSize: "0.9rem" }}
              >
                {el.date}
              </h6>
              <p className="card-text">{el.note}</p>
              <div className="row justify-content-end">
                {" "}
                <button
                  className="btn text-align-end"
                  style={{ width: "70px" }}
                  onClick={(e) => submit(e, el.note_id)}
                >
                  Erase
                </button>
              </div>
            </div>
          </div>
        ))
      )}
    </>
  );
}

export default memo(Note);
