import { useState } from "react";
import Nav from "./Nav";
function Register() {
  const [email, setEmail] = useState("");
  const [psw, setPsw] = useState("");
  const [type, setType] = useState("danger");
  const [message, setMessage] = useState("");
  const [alert, setAlert] = useState({ display: "none" });

  const sendMessage = (text, type = "danger", time = 4) => {
    setType(type);
    setMessage(text);
    setAlert({ display: "block" });
    setTimeout(() => {
      setAlert({ display: "none" });
    }, time * 1000);
  };
  //function for submit form
  const submit = (e) => {
    e.preventDefault();
     if (email.length < 3 || psw.length < 3) {
       return sendMessage("Inserisci i dati correttamente!");
     } else {
       sendMessage("Fatto.", "success");
     }
    fetch("/api/register/", {
      method: "POST",
      body: JSON.stringify({ email, psw }),
      headers: { "Content-Type": "application/json" },
    }).then(res => res.json()).then(data=> {
      if(data.status) sendMessage(data.message, 'success')
      else sendMessage(data.message)
    })
  };

  return (
    <>
      <Nav />

      <div className="container mt-5">
        <h2>Register</h2>
        <div className="card">
          <div className="card-body">
            <form>
              <div className="mb-3">
                <label htmlFor="email" className="form-label">
                  Email address
                </label>
                <input
                  onClick={(e) => setEmail(e.currentTarget.value)}
                  type="email"
                  className="form-control"
                  id="email"
                  aria-describedby="emailHelp"
                />
                <div id="emailHelp" className="form-text">
                  We'll never share your email with anyone else.
                </div>
              </div>
              <div className="mb-3">
                <label htmlFor="psw" className="form-label">
                  Password
                </label>
                <input
                  onClick={(e) => setPsw(e.currentTarget.value)}
                  type="password"
                  className="form-control"
                  id="psw"
                />
              </div>
              <div className="mb-3 form-check">
                <input
                  type="checkbox"
                  className="form-check-input"
                  id="exampleCheck1"
                />
                <label className="form-check-label" htmlFor="check">
                  Check me out
                </label>
              </div>
              <button
                onClick={(e) => submit(e)}
                type="submit"
                className="btn btn-primary"
              >
                Submit
              </button>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}

export default Register;
