import React from 'react'
import { useContext } from 'react';
import { useState } from 'react';
import { userContext } from './Home'
function CreateNote() {
  const {setType, setMessage, setAlert} = useContext(userContext);

  const [title, setTitle] = useState('');
  const [note, setNote] = useState("");
const sendMessage = (text, type = "danger", time = 4) => {
  setType(type);
  setMessage(text);
  setAlert({ display: "block" });
  setTimeout(() => {
    setAlert({ display: "none" });
    if (type === "success") {
      document.location.replace("/");
    }
  }, time * 1000);
};
  const submit = e =>{
    if (title.length < 3 || note.length < 3 ) return sendMessage('Please fill detail correctly')

  }

  return (
    <div className="container">
      <div className="card">
        <div className="card-body">
          <h4>Create new note</h4>
          <form>
            <div className="mb-3">
              <label htmlFor="title" className="form-label">
                Title
              </label>
              <input
                onChange={(e) => setTitle(e.currentTarget.value)}
                type="text"
                className="form-control"
                id="title"
                aria-describedby="emailHelp"
              />
            </div>
            <div className="mb-3">
              <label htmlFor="note" className="form-label">
                Note
              </label>
              <textarea
                className="form-control"
                id="exampleFormControlTextarea1"
                rows="3"
                onChange={(e) => setNote(e.currentTarget.value)}
              ></textarea>
            </div>
            <button
              onClick={(e) => submit(e)}
              type="submit"
              className="btn btn-primary"
            >
              Submit
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default CreateNote
